INSERT INTO `wp_options` VALUES (10929,'woo_framework_template','a:24:{i:0;a:3:{s:4:\"name\";s:14:\"Admin Settings\";s:4:\"icon\";s:7:\"general\";s:4:\"type\";s:7:\"heading\";}i:1;a:6:{s:4:\"name\";s:21:\"Super User (username)\";s:4:\"desc\";s:255:\"Enter your <strong>username</strong> to hide the Framework Settings and Update Framework from other users. Can be reset from the <a href=\"http://gacarrigan.wpengine.com/wp-admin/options.php\">WP options page</a> under <code>framework_woo_super_user</code>.\";s:2:\"id\";s:24:\"framework_woo_super_user\";s:3:\"std\";s:0:\"\";s:5:\"class\";s:4:\"text\";s:4:\"type\";s:4:\"text\";}i:2;a:5:{s:4:\"name\";s:21:\"Disable SEO Menu Item\";s:4:\"desc\";s:61:\"Disable the <strong>SEO</strong> menu item in the theme menu.\";s:2:\"id\";s:25:\"framework_woo_seo_disable\";s:3:\"std\";s:4:\"true\";s:4:\"type\";s:8:\"checkbox\";}i:3;a:5:{s:4:\"name\";s:33:\"Disable Sidebar Manager Menu Item\";s:4:\"desc\";s:73:\"Disable the <strong>Sidebar Manager</strong> menu item in the theme menu.\";s:2:\"id\";s:25:\"framework_woo_sbm_disable\";s:3:\"std\";s:4:\"true\";s:4:\"type\";s:8:\"checkbox\";}i:4;a:5:{s:4:\"name\";s:33:\"Disable Backup Settings Menu Item\";s:4:\"desc\";s:73:\"Disable the <strong>Backup Settings</strong> menu item in the theme menu.\";s:2:\"id\";s:32:\"framework_woo_backupmenu_disable\";s:3:\"std\";s:0:\"\";s:4:\"type\";s:8:\"checkbox\";}i:5;a:5:{s:4:\"name\";s:25:\"Theme Update Notification\";s:4:\"desc\";s:101:\"This will enable notices on your theme options page that there is an update available for your theme.\";s:2:\"id\";s:35:\"framework_woo_theme_version_checker\";s:3:\"std\";s:0:\"\";s:4:\"type\";s:8:\"checkbox\";}i:6;a:5:{s:4:\"name\";s:32:\"WooFramework Update Notification\";s:4:\"desc\";s:107:\"This will enable notices on your theme options page that there is an update available for the WooFramework.\";s:2:\"id\";s:39:\"framework_woo_framework_version_checker\";s:3:\"std\";s:0:\"\";s:4:\"type\";s:8:\"checkbox\";}i:7;a