INSERT INTO `wp_posts` VALUES (2257,2,'2013-05-25 10:01:51','2013-05-25 14:01:51','<h2><a href=\"http://www.westcoast-east.com/wp-content/uploads/CH-Privacy-Agreement2-e1369491454876.jpeg\"><img class=\"alignright size-full wp-image-2259\" alt=\"The Essential Handbag Illustration\" src=\"http://www.westcoast-east.com/wp-content/uploads/CH-Privacy-Agreement2-e1369491454876.jpeg\" width=\"100\" height=\"156\" /></a>Living Hands Free</h2>\r\nI\'ve been carrying my essentials bag for over a year now, taking it on shopping trips, dog walks, work and pretty much everywhere else. So I am my own test model. Every once in a while I find it a bit tight to hold everything, but I juggle things and swap out items I don\'t need at the time, such as a liptstick or business card case. It keeps me from adding extra notebooks, pens, ibuprofen bottles and everything else that I used to accumulate in a larger bag.\r\n\r\nIf I need more space, I just tuck my small bag into a tote and add my lunch and umbrella and whatever I need for the day.\r\n\r\nBy using a smaller bag, I\'ve been able to avoid the stress on my left shoulder which is constantly bothering me because of an old injury that has been exacerbated over the years by carrying heavy shoulder bags and laptop cases. I am no longer pulling up my shoulder bag or offloading it on my accommodating husband when I\'m looking at something or trying on clothes.\r\n\r\nIt\'s very freeing. I find that using a small bag keeps my hands and arms free to:\r\n<ul>\r\n	<li>Hug a friend</li>\r\n	<li>Hold a baby</li>\r\n	<li>Eat a sandwich</li>\r\n	<li>Ride a bike</li>\r\n	<li>Push a shopping cart</li>\r\n	<li>Shake hands</li>\r\n	<li>Pump gas</li>\r\n	<li>Flag a taxi</li>\r\n	<li>Take a photo</li>\r\n	<li>Walk the dog</li>\r\n</ul>','Look Ma! No Hands!','','publish','closed','closed','','look-ma-no-hands','','','2013-06-08 11:14:04','2013-06-08 15:14:04','',0,'http://www.westcoast-east.com/?p=2257',0,'post','',0);
INSERT INTO `wp_posts` VALUES (2258,2,'2013-06-08 11:14:47','2013-06-08 15:14:47','<h2><a href=\"http://www.westcoast-east.com/wp-content/uploads/CH-Privacy-Agreement2-e1369491454876.jpeg\"><img class=\"alignright size-full wp-image-2259\" alt=\"The Essential Handbag Illustration\" src=\"http://www.westcoast-east.com/wp-content/uploads/CH-Privacy-Agreement2-e1369491454876.jpeg\" width=\"100\" height=\"156\" /></a>Living Hands Free</h2>\r\nI\'ve been carrying my essentials bag for over a year now, taking it on shopping trips, dog walks, work and pretty much everywhere else. So I am my own test model. Every once in a while I find it a bit tight to hold everything, but I juggle things and swap out items I don\'t need at the time, such as a liptstick or business card case. It keeps me from adding extra notebooks, pens, ibuprofen bottles and everything else that I used to accumulate in a larger bag.\r\n\r\nIf I need more space, I just tuck my small bag into a tote and add my lunch and umbrella and whatever I need for the day.\r\n\r\nBy using a smaller bag, I\'ve been able to avoid the stress on my left shoulder which is constantly bothering me because of an old injury that has been exacerbated over the years by carrying heavy shoulder bags and laptop cases. I am no longer pulling up my shoulder bag or offloading it on my accommodating husband when I\'m looking at something or trying on clothes.\r\n\r\nIt\'s very freeing. I find that using a small bag keeps my hands and arms free to:\r\n<ul>\r\n	<li>Hug a friend</li>\r\n	<li>Hold a baby</li>\r\n	<li>Eat a sandwich</li>\r\n	<li>Ride a bike</li>\r\n	<li>Push a shopping cart</li>\r\n	<li>Shake hands</li>\r\n	<li>Pump gas</li>\r\n	<li>Flag a taxi</li>\r\n	<li>Take a photo</li>\r\n	<li>Walk the dog</li>\r\n</ul>','Look Ma! No Hands!','','inherit','closed','closed','','2257-autosave','','','2013-06-08 11:14:47','2013-06-08 15:14:47','',2257,'http://www.westcoast-east.com/2257-autosave/',0,'revision','',0);
